import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import NewsList from './components/NewsList'; // Asegúrate de que esta sea la página principal si Home no existe aún
import Home from './components/Home';
import NewsDetail from './components/NewsDetail';
import Register from './components/Auth/Register';
import Login from './components/Auth/Login';
import { AuthProvider } from './context/AuthContext'; // ¡Importante!

// import Footer from './components/Footer'; // Si tu proyecto tiene un Footer, impórtalo aquí.
import './App.css';

function App() {
  return (
    <Router>
      {/* AuthProvider DEBE ENVOLVER toda la aplicación (o al menos las partes que necesitan acceso al contexto de autenticación) */}
      <AuthProvider>
        {/* Usamos una div para envolver el Navbar, el main (con las rutas) y el Footer */}
        {/* Esta div no necesita la clase "App" a menos que tengas estilos CSS específicos para ella */}
        {/* La clase "container mt-4" se aplica mejor al div que envuelve las Rutas para centrar el contenido */}
        <Navbar /> {/* El Navbar se renderiza fuera de las rutas para que siempre esté visible */}
        <div className="container mt-4"> {/* Clase de Bootstrap para centrar el contenido principal */}
          <Routes> {/* <-- ¡TODAS tus rutas deben ir aquí dentro! */}
            {/* Si aún no tienes un Home.js completo, puedes usar NewsList como página de inicio por ahora */}
            {/* Si ya tienes Home.js, usa <Route path="/" element={<Home />} /> */}
            <Route path="/" element={<NewsList />} exact />
            <Route path="/news/:id" element={<NewsDetail />} />

            {/* ¡¡¡ESTAS SON LAS RUTAS QUE FALTAN O ESTÁN FUERA EN TU VERSIÓN ACTUAL!!! */}
            <Route path="/register" element={<Register />} />
            <Route path="/login" element={<Login />} />

            {/* Aquí irán las rutas para las nuevas categorías cuando las creemos */}
            <Route path="/articles" element={<div>Página de Artículos (Próximamente)</div>} />
            <Route path="/events-army" element={<div>Página de Eventos ARMY (Próximamente)</div>} />
            <Route path="/multimedia" element={<div>Página de Multimedia (Próximamente)</div>} />
            <Route path="/translations" element={<div>Página de Traducciones (Próximamente)</div>} />
            <Route path="/community" element={<div>Página de Comunidad (Próximamente)</div>} />
            <Route path="/merchandise" element={<div>Página de Mercancía (Próximamente)</div>} />

          </Routes>
        </div>
        {/* Si tienes un Footer, puedes agregarlo aquí. No es necesario que esté dentro del "container" */}
        {/* {Footer && <Footer />} */}
      </AuthProvider>
    </Router>
  );
}

export default App;
